mapper.insert:
INSERT
INTO MEMBER_INFO
(
    MEMBER_NAME
    , BIRTH_DATE
    , GENDER
    , DETAIL_INFO
    , PHONE
    , TEAM_CODE
    , ACTIVE_STATUS
)
VALUES
    (
        #{ memberName }
    , #{ birthDate }
    , #{ gender }
    , #{ detailInfo }
    , #{ phone }
    , #{ teamCode }
    , 'Y'
    )

mapper.update:
UPDATE
    MEMBER_INFO
SET PHONE = #{ phone }
    ,ACTIVE_STATUS = #{ activeStatus }
WHERE MEMBER_CODE = #{ memberCode }

mapper.delete:
DELETE
FROM MEMBER_INFO
WHERE MEMBER_CODE = #{ memberCode }
